<template>
	<j-image @click="clickHandler" class="j-avatar--image" :src="avatarUrl || defaultUrl" :width="size" :height="size" :shape="shape" :mode="mode" @error="errorHandler">
	</j-image>
</template>
<script>
	const base64Avatar = "data:image/jpg;base64,";
	export default {
		name: 'j-avatar',
		props: {
			// 头像图片路径(不能为相对路径)
			src: {
				type: String,
				default: ''
			},
			// 头像形状，circle-圆形，square-方形
			shape: {
				type: String,
				default: 'circle'
			},
			// 头像尺寸
			size: {
				type: [String, Number],
				default: '80rpx'
			},
			// 裁剪模式
			mode: {
				type: String,
				default: 'scaleToFill'
			},
			// 加载失败的默认头像
			defaultUrl: {
				type: String,
				default: ''
			},
		},
		data() {
			return {
				avatarUrl: this.src
			}
		},
		watch: {
			// 监听头像src的变化，赋值给内部的avatarUrl变量，因为图片加载失败时，需要修改图片的src为默认值
			// 而组件内部不能直接修改props的值，所以需要一个中间变量
			src: {
				immediate: true,
				handler(newVal) {
					this.avatarUrl = newVal
					// 如果没有传src，则主动触发error事件，用于显示默认的头像，否则src为''空字符等的时候，会无内容展示
					if (!newVal) {
						this.errorHandler()
					}
				}
			}
		},
		methods: {
			// 图片加载时失败时触发
			errorHandler() {
				this.avatarUrl = this.defaultUrl || base64Avatar
			},
			clickHandler(event) {
				// #ifndef H5||APP
				this.$emit('click',event);
				// #endif
			}
		}
	}
</script>